mapper.select:
select c.crew_number, c.crew_name, c.introduction, c.INTEREST_CATEGORY, c.image, g.gu_name, cm_count.crew_members_count from crews c join gues g on c.gu_number = g.gu_number join ( select c.crew_number, count(c.crew_number) + 1 as crew_members_count from crews c left join crew_members cm on c.crew_number = cm.crew_number group by c.crew_number ) cm_count on c.crew_number = cm_count.crew_number WHERE 1=1
AND c.interest_category = #{interestCategory}
AND g.gu_name = #{guName}
AND c.age_range = #{ageRange}

mapper.update:
UPDATE crew_meetings
SET title = #{title}, content = #{content}, image = #{image},
meeting_date = #{meetingDate},
max_member = #{maxMember}, gu_number =
#{guNumber}
WHERE crew_meeting_number = #{crewMeetingNumber}

mapper.delete:
delete crew_applies
where crew_apply_number = #{crewApplyNumber} and crew_number=
#{crewNumber}
